mapper.select:
select id, a.cust_id cust_id, b.cust_name cust_name,user_id, to_object, score, content, createtime, visible from evaluation a join (select cust_id,cust_name from customer
where cust_name like "%"#{cust_name}"%"
) b on a.cust_id=b.cust_id
order by createtime desc
limit #{start},#{rows}

select count(*) from evaluation a join (select cust_id,cust_name from customer
where cust_name like "%"#{cust_name}"%"
) b on a.cust_id=b.cust_id
