mapper.insert:
insert into follow_status (cust_id, cust_status, business_status, 
  follow_status, cust_property, times, createtime
  )
values (#{cust_id,jdbcType=BIGINT}, #{cust_status,jdbcType=VARCHAR}, #{business_status,jdbcType=VARCHAR},
  #{follow_status,jdbcType=INTEGER}, #{cust_property,jdbcType=VARCHAR}, #{times,jdbcType=BIGINT}, #{createtime,jdbcType=TIMESTAMP}
  )

mapper.select:
SELECT cust_id, b.item_name cust_status, c.item_name business_status, d.item_name cust_property, f.cust_name cust_name, f.sys_user_name cust_user_name, f.cust_linkman cust_linkman, follow_status FROM follow_status a LEFT JOIN ( SELECT id, item_name FROM base_dict WHERE type_code = '005' ) b ON a.cust_status = b.id LEFT JOIN ( SELECT id, item_name FROM base_dict WHERE type_code = '007' ) c ON a.business_status = c.id LEFT JOIN ( SELECT id, item_name FROM base_dict WHERE type_code = '003' ) d ON a.cust_property = d.id JOIN ( SELECT g.cust_id cid, cust_name, cust_linkman, cust_user_id, e.sys_user_name sys_user_name FROM customer g LEFT JOIN ( SELECT id, sys_user_name FROM admin_user ) e ON g.cust_user_id = e.id
where g.cust_name like "%"#{cust_name}"%"
) f ON a.cust_id = f.cid
ORDER BY createtime DESC
limit #{start},#{rows}

select count(*) from follow_status a JOIN ( SELECT g.cust_id cid, cust_name, cust_linkman, cust_user_id, e.sys_user_name sys_user_name FROM customer g LEFT JOIN ( SELECT id, sys_user_name FROM admin_user ) e ON g.cust_user_id = e.id
where g.cust_name like "%"#{cust_name}"%"
) f ON a.cust_id = f.cid
