mapper.select:
SELECT AVG(score) scoreAvg,COUNT(*) num,to_object from evaluation where cust_id = #{cust_id}
and createtime BETWEEN DATE_FORMAT(#{start_time},"%Y-%m-01") and DATE_FORMAT(#{end_time},"%Y-%m-01")
GROUP BY to_object

SELECT IFNULL(c.item_name,"其它") name,COUNT(*) value from follow_record a LEFT JOIN ( SELECT id, item_name FROM base_dict WHERE type_code = '011' ) c ON a.way = c.id WHERE cust_id = #{cust_id}
and record_time BETWEEN DATE_FORMAT(#{start_time},"%Y-%m-01") and DATE_FORMAT(#{end_time},"%Y-%m-01")
GROUP BY c.item_name

set @i = -1;
set @j = -1;
SELECT b.date as month ,ifnull(a.fre,0) as fre
FROM(
SELECT COUNT(*) fre ,DATE_FORMAT(record_time,"%Y-%m") as month
FROM follow_record
WHERE cust_id=#{cust_id}
GROUP BY
DATE_FORMAT(record_time,"%Y-%m")
) a
RIGHT JOIN
(SELECT DATE_FORMAT(ADDDATE(#{start_time}, INTERVAL @j:=@j+1 MONTH),"%Y-%m") AS date FROM customer c2
HAVING ADDDATE(#{start_time}, INTERVAL @i:=@i+1 MONTH) < #{end_time}
) b on b.date = a.month
HAVING  b.date < DATE_FORMAT(#{end_time},"%Y-%m")
order by month asc

set @i = -1;
set @j = -1;
SELECT b.date as month ,ifnull(a.money,0) as money
FROM(
SELECT sum(money) money ,DATE_FORMAT(start_time,"%Y-%m") as month
FROM contract
WHERE cust_id=#{cust_id}
GROUP BY
DATE_FORMAT(start_time,"%Y-%m")
) a
RIGHT JOIN
(SELECT DATE_FORMAT(ADDDATE(#{start_time}, INTERVAL @j:=@j+1 MONTH),"%Y-%m") AS date FROM customer c2
HAVING ADDDATE(#{start_time}, INTERVAL @i:=@i+1 MONTH) < #{end_time}
) b on b.date = a.month
HAVING  b.date < DATE_FORMAT(#{end_time},"%Y-%m")
order by month asc
